// SPDX-License-Identifier: MIT
pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;
contract MedicalBills {
    event BillAdded(
        string patientId,
        string patientName,
        string billType,
        uint amount
    );

    struct Bill {
        string patientName;
        string billType;
        uint amount;
    }

    mapping(string => Bill) public bills;

    function addBill(string memory patientId, string memory patientName, string memory billType, uint amount) public {
        bills[patientId] = Bill(patientName, billType, amount);
        emit BillAdded(patientId, patientName, billType, amount);
    }
}
